const canvas = document.getElementById("myCanvas");
const ctx = canvas.getContext("2d");// Create a circular 
  clipping regionctx.beginPath();ctx.arc(100, 75, 70, 0, Math.PI * 2);
  ctx.clip();// Draw two rectanglesctx.fillStyle = "blue";
  ctx.fillRect(0, 0, 300, 150);ctx.fillStyle = "red";ctx.fillRect(0, 0, 
  90, 90);

