const canvas = document.getElementById("myCanvas");
const ctx = canvas.getContext("2d");const image = 
  document.getElementById("scream");image.addEventListener("load", (e) 
  => {  // Create a circular clipping region  ctx.beginPath();  
  ctx.arc(110, 145, 75, 0, Math.PI * 2);  ctx.clip();  // Draw 
  image onto canvas  
  ctx.drawImage(image, 0, 0);});

