const canvas = document.getElementById("myCanvas");
const ctx = canvas.getContext("2d");
  ctx.beginPath();

// Define a rectangle
ctx.moveTo(20,20);
ctx.lineTo(180,20);
ctx.lineTo(180,100);
ctx.lineTo(20,100);
ctx.lineTo(20,20);

// Define a triangle
ctx.moveTo(100,20);
ctx.lineTo(180,100);
ctx.lineTo(20,100);
ctx.lineTo(100,20);

ctx.strokeStyle = "red";
ctx.stroke();

