/* Place the navbar at the bottom of the page, and make it stick */.navbar {  background-color: #333;  overflow: hidden;  position: fixed;  bottom: 0;  width: 
  100%;}/* 
Style the links inside the navigation bar */.navbar a {  float: left;  display: block;  color: #f2f2f2;  text-align: center;
    
  padding: 14px 16px;  text-decoration: none;  font-size: 17px;}/* Change the color of links on hover */
  .navbar a:hover {  background-color: #ddd;  
  color: black;}/* Add a green background color to the active link 
  */.navbar 
  a.active {  background-color: #04AA6D;  
  color: white;}/* Hide the link that should open and close the 
  navbar on small screens */
  .navbar .icon {  display: none;}

