syntaxHighlighting.text:
/* 
  Create a top navigation bar with a black background color  */.topnav {  background-color: #333;
    overflow: hidden;}/* 
Style the links inside the navigation bar */.topnav a {  
  float: left;  color: #f2f2f2;  text-align: center;  padding: 14px 16px;  text-decoration: none;
    
  font-size: 17px;}/* Change the color of links on hover */.topnav a:hover {
    
  background-color: #ddd;  color: black;}/* Create a 
  right-aligned (split) link inside the navigation bar */.topnav 
  a.split {  float: right;  background-color: #04AA6D;  color: white;}

