Scanner
myObj
=

new

Scanner
(
"Please send an email to info@example.com for more details."
)
;



// Get the email address with a pattern


String
email
=
myObj
.
findWithinHorizon
(
"[a-zA-Z]+@[a-zA-Z]+.[a-zA-Z]{2,}"
,

0
)
;



// Show the email if found


if

(
email
!=

null
)

{


System
.
out
.
println
(
email
)
;


}

else

{


System
.
out
.
println
(
"No email found"
)
;


}
