  const app = Vue.createApp({ 
        data() { 
            return { 
                tigers: 0, 
                moose: 0, 
                kangaroos: 0 
            } 
          }, 
        methods: { 
            addAnimal(e,number) { 
                if(e.target.parentElement.id==="tigers") { 
                    this.tigers+=number 
                } 
                else if(e.target.parentElement.id==="moose") { 
                    this.moose+=number 
                } 
                else { 
                    this.kangaroos+=number 
                } 
            } 
        } 
    }) 
 app.mount('#app')

